package ao.jobera.com.e_diaristas.web.DTOs;import jakarta.validation.constraints.NotEmpty;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructor@AllArgsConstructorpublic class UsuarioCadastroForm {    @NotNull    @Size(min = 5, max = 250, message = "O nome tem que ter pelo menos 5 caracteres")    private String nomeCompleto;    @NotNull    @Size(min = 5, max = 250, message = "O email é obrigatório")    private String email;    @NotNull    @Size(min = 8, max = 16, message = "A senha é obrigatório")    @NotEmpty    private String senha;    @NotNull    @Size(min = 8, max = 16, message = "A confirmação de senha é obrigatório")    @NotEmpty    private String confirmSenha;}